度                } else {//如果图片真实高度和宽度都符合要求，高宽不变                    imgWidth = realWidth;                    imgHeight = realHeight;                }                $(bigimg).css("width", imgWidth);//以最终的宽度对图片缩放                var w = (windowW - imgWidth) / 2;//计算图片与窗口左边距                var h = (windowH - imgHeight) / 2;//计算图片与窗口上边距                $(innerdiv).css({"top": h, "left": w});//设置#innerdiv的top和left属性                $(outerdiv).fadeIn("fast");//淡入显示#outerdiv及.pimg            });            $(outerdiv).click(function () {//再次点击淡出消失弹出层                $(this).fadeOut("fast");            });        }        function appendMask(content, src) {            $("body").append(`                <div class="maskNew">                    <div class="maskNewContent">                        <a href="javascript:void(0);" class="closeA" onclick=closeMask1() ><img src="images/icon1/gb.png"/></a>                        <div class="maskTitle">删除订单</div>                           <div style="text-align:center;margin-top:30px"><img src="images/icon1/${src}.png"></div>                        <div style="height: 50px;position: relative;top:20px;font-size: 22px;text-align: center;">                            ${content}                        </div>                        <div style="text-align:center;margin-top:30px">                            <button class="closeMask" onclick=closeMask1() >确认</button>                        </div>                                            </div>                </div>              `)        };        function closeMask(id, url, content) {            $.ajax({                type: "post",                url: url + id,                async: true,                dataType: "json",                success: function (res) {                    if (res.status == 1) {                        layer.msg("删除成功")                        // location.replace(location.href);                    }                    else {                        layer.msg("删除失败");                    }                }            });        }        function confirm(content, id, url, content1) {            $("body", parent.document).append(`                <div class="maskNew">                    <div class="maskNewContent">                        <a href="javascript:void(0);" class="closeA" onclick=closeMask1() ><img src="images/icon1/gb.png"/></a>                        <div class="maskTitle">提示</div>                         <div style="text-align:center;margin-top:30px"><img src="images/icon1/ts.png"></div>                        <div style="height: 50px;position: relative;top:20px;font-size: 22px;text-align: center;">                            ${content}                        </div>                        <div style="text-align:center;margin-top:30px">                            <button class="closeMask" style="margin-right:20px" onclick=closeMask("${id}","${url}","${content1}") >确认</button>                            <button class="closeMask" onclick=closeMask1() >取消</button>                        </div>                    </div>                </div>              `)        };    </script>'; ?>
