<?phprequire_once(MO_LIB_DIR . '/DBAction.class.php');require_once(MO_LIB_DIR . '/ShowPager.class.php');require_once(MO_LIB_DIR . '/Tools.class.php');require_once(MO_LIB_DIR . '/ServerPath.class.php');require_once(MO_LIB_DIR . '/JurisdictionAction.class.php');class IndexAction extends Action {    /**     * <p>Copyright (c) 2018-2019</p>     * <p>Company: www.laiketui.com</p>     * @author 段宏波     * @date 2018/12/12  18:54     * @version 1.0     */    public function getDefaultView() {        $db = DBAction::getInstance();        $JurisdictionAction = new JurisdictionAction();        $request = $this->getContext()->getRequest();        $store_id = $this->getContext()->getStorage()->read('store_id');        $top_type = $this->getContext()->getStorage()->read('store_type');        $admin_name = $this->getContext()->getStorage()->read('admin_name'); // 管理员id        $admin_type1 = $this->getContext()->getStorage()->read('admin_type1');        $button[0] = $JurisdictionAction->Jurisdiction($store_id,$admin_name,$admin_type1,'index.php?module=banner&action=Add');        $button[1] = $JurisdictionAction->Jurisdiction($store_id,$admin_name,$admin_type1,'index.php?module=banner&action=Modify');        $button[2] = $JurisdictionAction->Jurisdiction($store_id,$admin_name,$admin_type1,'index.php?module=banner&action=Del');        $button[3] = $JurisdictionAction->Jurisdiction($store_id,$admin_name,$admin_type1,'index.php?module=banner&action=Stick');        $pagesize = $request -> getParameter('pagesize');        $pagesize = $pagesize ? $pagesize:'10';        // 每页显示多少条数据        $page = $request -> getParameter('page');        // 页码        if($page){            $start = ($page-1)*$pagesize;        }else{            $start = 0;        }        $sql = "select * from lkt_banner where store_id = '$store_id' and type = '$top_type'";        $r = $db->select($sql);        $total = count($r);        $pager = new ShowPager($total,$pagesize,$page);        // 查询轮播图表，根据sort顺序排列        $sql = "select * from lkt_banner where store_id = '$store_id' and type = '$top_type' order by sort desc limit $start,$pagesize";        $r = $db->select($sql);        foreach ($r as $k => $v) {            $v->image = ServerPath::getimgpath($v->image,$store_id);        }        $url = "index.php?module=banner&action=Index&pagesize=".urlencode($pagesize);        $pages_show = $pager->multipage($url,$total,$page,$pagesize,$start,$para = '');        $request->setAttribute("list",$r);        $request -> setAttribute('pages_show', $pages_show);        $request->setAttribute('button', $button);        return View :: INPUT;    }    public function execute() {    }    public function getRequestMethods(){        return Request :: NONE;    }}?>