据字典ID为'.$v.'成功',3);                    $Log_content = __METHOD__ . '->' . __LINE__ . ' 删除数据字典ID为'.$v.'成功 ';                    $lktlog->customerLog($Log_content);                }else{                    $JurisdictionAction->admin_record($store_id,$admin_name,'删除数据字典ID为'.$v.'失败',3);                    $Log_content = __METHOD__ . '->' . __LINE__ . ' 删除数据字典ID为'.$v.'失败 ';                    $lktlog->customerLog($Log_content);                    $db->rollback();                    echo json_encode(array('status' => '0'));exit;                }            }else{                $JurisdictionAction->admin_record($store_id,$admin_name,'删除数据字典ID为'.$v.'失败。'.$rew['msg'],3);                $Log_content = __METHOD__ . '->' . __LINE__ . ' 删除数据字典ID为'.$v.'失败。'.$rew['msg'];                $lktlog->customerLog($Log_content);                $db->rollback();                echo json_encode(array('status' =>2 ,'info'=>$rew['msg']));exit;            }        }        $db->commit();        echo json_encode(array('status' =>1));exit;        return;    }    public function execute(){        return $this->getDefaultView();    }    public function getRequestMethods(){        return Request :: NONE;    }}?>