<?phprequire_once(MO_LIB_DIR . '/DBAction.class.php');require_once(MO_LIB_DIR . '/JurisdictionAction.class.php');require_once(MO_LIB_DIR . '/LaiKeLogUtils.class.php');class Modify_nameAction extends Action {    /**     * <p>Copyright (c) 2018-2019</p>     * <p>Company: www.laiketui.com</p>     * @author 段宏波     * @date 2018/12/12  18:54     * @version 1.0     */	public function getDefaultView() {        $db = DBAction::getInstance();        $request = $this->getContext()->getRequest();        // 接收信息        $id = intval($request->getParameter("id")); // 轮播图id        $arr = array();        $sql0 = "select * from lkt_data_dictionary_name where id = '$id'";        $r0 = $db->select($sql0);        if($r0){            $name = $r0[0]->name;            $status = $r0[0]->status;            $arr = array('id'=>$id,'name'=>$name,'status'=>$status);        }        echo json_encode($arr);exit;        return View :: INPUT;	}    /**     * <p>Copyright (c) 2018-2019</p>     * <p>Company: www.laiketui.com</p>     * @author 段宏波     * @date 2018/12/12  18:54     * @version 1.0     */	public function execute(){		$db = DBAction::getInstance();        $JurisdictionAction = new JurisdictionAction();        $lktlog = new LaiKeLogUtils("common/data_dictionary.log");        // 1.开启事务        $db->begin();		$request = $this->getContext()->getRequest();        $store_id = $this->getContext()->getStorage()->read('store_id');        $admin_name = $this->getContext()->getStorage()->read('admin_name');        // 接收信息		$id = intval($request->getParameter('id'));        $name = addslashes($request->getParameter('name')); // 数据名称        $status = addslashes($request->getParameter('status')); // 是否生效        if($name == ''){            echo json_encode(array('status' =>'数据名称不能为空！' ));exit;        }else{            $sql0 = "select * from lkt_data_dictionary_name where name = '$name' and recycle = 0 and id != '$id'";            $r0 = $db->select($sql0);            if($r0){                echo json_encode(array('status' =>'数据名称'.$name.'已存在！' ));exit;            }        }        $sql2 = "update lkt_data_dictionary_name set name = '$name',status = '$status',admin_name = '$admin_name',add_date = CURRENT_TIMESTAMP where id = '$id'";        $r2 = $db->update($sql2);        if($r2 == -1){            $JurisdictionAction->admin_record($store_id,$admin_name,'修改数据字典名称ID为'.$id.'失败',2);            $Log_content = __METHOD__ . '->' . __LINE__ . ' 修改数据字典名称失败ID为'.$id.'失败';            $lktlog->customerLog($Log_content);            $db->rollback();            echo json_encode(array('status' => '未知原因，修改失败！'));exit;        }else{            $JurisdictionAction->admin_record($store_id,$admin_name,'修改数据字典名称ID为'.$id.'成功',2);            $Log_content = __METHOD__ . '->' . __LINE__ . ' 修改数据字典名称ID为'.$id.'成功 ';            $lktlog->customerLog($Log_content);            $db->commit();            echo json_encode(array('status' =>'修改成功！' ,'suc'=>'1'));exit;        }		return;	}	public function getRequestMethods(){		return Request :: POST;	}}?>