<?phpclass ModifyInputView extends SmartyView {    public function execute() {		$request = $this->getContext()->getRequest();		$this->setAttribute('id',$request->getAttribute('id'));        $this->setAttribute('code',$request->getAttribute('code'));        $this->setAttribute('name',$request->getAttribute('name'));        $this->setAttribute('s_name',$request->getAttribute('s_name'));        $this->setAttribute('value',$request->getAttribute('value'));        $this->setAttribute('text',$request->getAttribute('text'));        $this->setAttribute('status',$request->getAttribute('status'));        $this->setAttribute('data_dictionary_list',$request->getAttribute('data_dictionary_list'));		$this->setTemplate("Modify.tpl");    }}?>